    def file_select(self):
        self.folder_way = QFileDialog.getExistingDirectory(None, 'OpenFile','.')
        self.label_12.setText(self.folder_way)
        print(self.folder_way)
        return self.folder_way
    
    def data_output(self):
        self.folder_out_way = QFileDialog.getExistingDirectory(None, 'OpenFile','.')
        self.label_11.setText(self.folder_out_way)
        print(self.folder_out_way)
        if not os.path.exists( self.folder_out_way+'/total_img'):
            os.makedirs(self.folder_out_way+'/total_img')
        return self.folder_out_way


    
    def img_prez(self , directory):
        sum_img=0
        stat=[['index','outer','inner','(outer-inner)/2']]
        sort= natsort.natsorted(os.listdir(directory))
        name_out=self.folder_out_way+'/tab.csv'

        for filename in sort:
            f = os.path.join(directory, filename)
            sum_img+=1
            if os.path.isfile(f) and sum_img!=5 and sum_img!=6 and sum_img!=7 : 
                print(f)
                o,i,cir1,cir2,imgt =func(f , self.settings) 

                stat.append([sum_img,o,i,((o-i)/2)])
                cv2.waitKey(1)
                cv2.imwrite( self.folder_out_way+'/total_img/pic'+str(sum_img)+'.jpg', imgt )
                
                #Вывод Изображений 
                pixway = QPixmap(self.folder_out_way+'/total_img/pic'+str(sum_img)+'.jpg')
                self.label.setPixmap(QtGui.QPixmap(pixway))

                #Заполнение таблицы
                oi=str((o-i)/2)
                i=str(i)
                o=str(o)
                rowPosition = self.table.rowCount()
                new=rowPosition
                self.table.insertRow(rowPosition)
                self.table.setItem(new, 0, QTableWidgetItem(o)) 
                self.table.setItem(new, 1, QTableWidgetItem(i)) 
                self.table.setItem(new, 2, QTableWidgetItem(oi))        

        k , b ,max_mid = data_processing(stat)
        print ( 'Уравнение: ', k,'* x +',b ,'\nСреднее значение максимумов ( > 90% от максимального значения) ', max_mid )

        with open(name_out, mode="w", encoding='utf-8') as w_file:
            file_writer = csv.writer(w_file)
            for line in stat:
                file_writer.writerow(line)            
        stat = pd.DataFrame(stat)     
        writer_obj = pd.ExcelWriter(self.folder_out_way+'/tab.xlsx',engine='xlsxwriter')
        stat.to_excel(writer_obj, sheet_name='1')
        writer_obj.save()

        pixway2 = QPixmap(graph(name_out ,self.folder_out_way ))
        self.label_2.setPixmap(QtGui.QPixmap(pixway2))

        return name_out
    

    def warning_box(self , cod):
        msg = QMessageBox()
        msg.setWindowTitle("Warning")
        if cod == 1:
            msg.setText("Укажите путь до папки с измеряемыми образцами!")
        elif cod == 2 :
            msg.setText("Укажите путь до места сохранения результатов!")
        msg.exec()

    def add_functions(self):
        self.pushButton.clicked.connect(lambda :
                                         self.warning_box(cod=(1 if self.folder_way == None   else ( 2 if self.folder_out_way ==None else None))) if (self.folder_way == None or self.folder_out_way ==None)  else self.img_prez(self.folder_way) )
        
        self.pushButton_2.clicked.connect(lambda : print(self.settings))
        self.toolButton.clicked.connect(lambda : self.file_select())
        self.toolButton_2.clicked.connect(lambda : self.data_output())
        self.pushButton_3.clicked.connect(lambda : sys.exit() )
    

